{"changed":true,"filter":false,"title":"menu_controller.rb","tooltip":"/controllers/menu_controller.rb","value":"require_relative '../models/address_book.rb'\n\nclass MenuController\n    attr_reader :address_book\n    \n    def initialize\n        @address_book = AddressBook.new\n    end\n    \n    def main_menu\n        puts \"Main Menu - #{address_book.entries.count} entries\"\n        puts \"1 - View all entries\"\n        puts \"2 - Create an entry\"\n        puts \"3 - Search for an entry\"\n        puts \"4 - Import entries from a CSV\"\n        puts \"5 - View Entry Number n\"\n        puts \"6 - Exit\"\n        print \"Enter your selection: \"\n        \n        selection = gets.to_i\n        puts \"You selected #{selection}\"\n        \n        case selection\n            when 1\n                system \"clear\"\n                view_all_entries\n                main_menu\n            when 2\n                system \"clear\"\n                create_entry\n                main_menu\n            when 3\n                system \"clear\"\n                search_entries\n                main_menu\n            when 4\n                system \"clear\"\n                read_csv\n                main_menu\n            when 5\n                system \"clear\"\n                view_entry_n\n                main_menu\n            when 6\n                puts \"Good-bye!\"\n                exit(0)\n            else\n                system \"clear\"\n                puts \"Sorry, that is not a valid input\"\n                main_menu\n        end\n    end\n    \n    def view_all_entries\n        address_book.entries.each do |entry|\n            system \"clear\"\n            puts entry.to_s\n            \n            entry_submenu(entry)\n        end\n        \n        system \"clear\"\n        puts \"End of Entries\"\n    end\n    \n    def create_entry\n        system \"clear\"\n        puts \"New AddressBloc Entry\"\n        \n        print \"Name: \"\n        name = gets.chomp\n        \n        print \"Phone number: \"\n        phone = gets.chomp\n        \n        print \"Email: \"\n        email = gets.chomp\n        \n        address_book.add_entry(name, phone, email)\n        \n        system \"clear\"\n        puts \"New Entry Created\"\n    end\n    \n    def search_entries\n        \n    end\n    \n    def read_csv\n        \n    end\n    \n    def view_entry_n\n       system \"clear\"\n       print \"Enter entry number: \"\n       \n       selection = gets.chomp.to_i\n       \n       if selection > @address_book.size\n            system \"clear\"\n            puts \"Sorry, that is not a valid input\"\n            view_entry_n\n        else\n            print @address_book[selection+1]\n            main_menu\n        end\n    end\n    \n    def entry_submenu(entry)\n        \n        puts \"n - next entry\"\n        puts \"d - delete entry\"\n        puts \"e - edit this entry\"\n        puts \"m - return to main menu\"\n        \n        selection = gets.chomp\n        \n        case selection\n            when \"n\"\n            \n            when \"d\"\n                \n            when \"e\"\n                \n            when \"m\"\n                system \"clear\"\n                main_menu\n            else\n                system \"clear\"\n                puts \"#{selection} is not a valid input\"\n                entry_submenu(entry)\n        end\n    end\nend","undoManager":{"mark":-2,"position":3,"stack":[[{"start":{"row":94,"column":32},"end":{"row":94,"column":33},"action":"insert","lines":[" "],"id":686},{"start":{"row":94,"column":33},"end":{"row":94,"column":34},"action":"insert","lines":["("]}],[{"start":{"row":94,"column":34},"end":{"row":94,"column":35},"action":"insert","lines":[" "],"id":687}],[{"start":{"row":94,"column":34},"end":{"row":94,"column":35},"action":"remove","lines":[" "],"id":688},{"start":{"row":94,"column":33},"end":{"row":94,"column":34},"action":"remove","lines":["("]},{"start":{